package ejerciciosSintaxis;

public class MetodosParse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cadena1="100";
		String cadena2="5";
		String cadena3="900";
		int numero1=Integer.parseInt(cadena1); // 100
		int numero2=Integer.parseInt(cadena2); // 5
		System.out.println(numero1+numero2);
		double numero3=Double.parseDouble(cadena3);
		System.out.println(numero3);
	}

}
